rlo)		#IMPLIED\n	xml:base	CDATA		#IMPLIED\n\n	class       CDATA          #IMPLIED\n	style       CDATA          #IMPLIED\n	title       CDATA         #IMPLIED\n	xml:lang    NMTOKEN        #IMPLIED\n	onclick     CDATA       #IMPLIED\n  ondblclick  CDATA       #IMPLIED\n  onmousedown CDATA       #IMPLIED\n  onmouseup   CDATA       #IMPLIED\n  onmouseover CDATA       #IMPLIED\n  onmousemove CDATA       #IMPLIED\n  onmouseout  CDATA       #IMPLIED\n  onkeypress  CDATA       #IMPLIED\n  onkeydown   CDATA       #IMPLIED\n  onkeyup     CDATA       #IMPLIED\n	role		CDATA		#IMPLIED\n>\n\n<!--doc:Specifications for a column in a table.-->\n<!ELEMENT colspec  EMPTY >\n\n<!ATTLIST colspec\n        colnum          CDATA                                   #IMPLIED\n        colname         CDATA                                   #IMPLIED\n        colwidth        CDATA                                   #IMPLIED\n        colsep          CDATA                               #IMPLIED\n        rowsep          CDATA                               #IMPLIED\n        align           (left|right|center|justify|char)        #IMPLIED\n        char            CDATA                                   #IMPLIED\n        charoff         CDATA                                   #IMPLIED\n>\n\n<!--doc:Formatting information for a spanned column in a table.-->\n<!ELEMENT spanspec  EMPTY >\n\n<!ATTLIST spanspec\n        namest          CDATA                                   #REQUIRED\n        nameend         CDATA                                   #REQUIRED\n        spanname        CDATA                                   #REQUIRED\n        colsep          CDATA                               #IMPLIED\n        rowsep          CDATA                               #IMPLIED\n        align           (left|right|center|justify|char)        #IMPLIED\n        char            CDATA                                   #IMPLIED\n        charoff         CDATA                                   #IMPLIED\n>\n\n<!--doc:A table header consisting of one or more rows.-->\n<!ELEMENT thead  ((tr+|(colspec*,row+)))>\n<!ATTLIST thead\n        valign          (top|middle|bottom)                     #IMPLIED\n\n	id		ID		#IMPLIED\n	lang		CDATA		#IMPLIED\n	remap		CDATA		#IMPLIED\n	xreflabel	CDATA		#IMPLIED\n	revisionflag	(changed\n			|added\n			|deleted\n			|off)		#IMPLIED\n	arch		CDATA		#IMPLIED\n        condition	CDATA		#IMPLIED\n	conformance	NMTOKENS	#IMPLIED\n	os		CDATA		#IMPLIED\n	revision	CDATA		#IMPLIED\n        security	CDATA		#IMPLIED\n	userlevel	CDATA		#IMPLIED\n	vendor		CDATA		#IMPLIED\n	wordsize	CDATA		#IMPLIED\n\n	dir		(ltr\n			|rtl\n			|lro\n			|rlo)		#IMPLIED\n	xml:base	CDATA		#IMPLIED\n\n	class       CDATA          #IMPLIED\n	style       CDATA          #IMPLIED\n	title       CDATA         #IMPLIED\n	xml:lang    NMTOKEN        #IMPLIED\n	onclick     CDATA       #IMPLIED\n  ondblclick  CDATA       #IMPLIED\n  onmousedown CDATA       #IMPLIED\n  onmouseup   CDATA       #IMPLIED\n  onmouseover CDATA       #IMPLIED\n  onmousemove CDATA       #IMPLIED\n  onmouseout  CDATA       #IMPLIED\n  onkeypress  CDATA       #IMPLIED\n  onkeydown   CDATA       #IMPLIED\n  onkeyup     CDATA       #IMPLIED\n	role		CDATA		#IMPLIED\n>\n\n<!--doc:A table footer consisting of one or more rows.-->\n<!ELEMENT tfoot  ((tr+|(colspec*,row+)))>\n<!ATTLIST tfoot\n        valign          (top|middle|bottom)                     #IMPLIED\n\n	id		ID		#IMPLIED\n	lang		CDATA		#IMPLIED\n	remap		CDATA		#IMPLIED\n	xreflabel	CDATA		#IMPLIED\n	revisionflag	(changed\n			|added\n			|deleted\n			|off)		#IMPLIED\n	arch		CDATA		#IMPLIED\n        condition	CDATA		#IMPLIED\n	conformance	NMTOKENS	#IMPLIED\n	os		CDATA		#IMPLIED\n	revision	CDATA		#IMPLIED\n        security	CDATA		#IMPLIED\n	userlevel	CDATA		#IMPLIED\n	vendor		CDATA		#IMPLIED\n	wordsize	CDATA		#IMPLIED\n\n	dir		(ltr\n			|rtl\n			|lro\n			|rlo)		#IMPLIED\n	xml:base	CDATA		#IMPLIED\n\n	class       CDATA          #IMPLIED\n	style       CDATA          #IMPLIED\n	title    